import { Grid, ListItem, ListItemText, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { useState } from 'react';
import { useEffect } from 'react';
import { useChats, useUsers } from '../hooks/apiHooks';

interface propType {
    user_id: number,
    contents: string,
    timestamp: Date,
}

const useStyles = makeStyles(() => ({
    root: {
        width: '100%',
        backgroundColor: 'lightGrey',
        marginBottom: '1rem',
        borderRadius: '1rem',
        margin: 'auto',
    },
    inline: {
        display: 'inline',
    },
    timestamp: {
        fontSize: '0.7rem'
    }
}));

const Message = ({ user_id, contents, timestamp }: propType) => {
    const classes = useStyles();
    const [username, setUsername] = useState('');
    const [time, setTime] = useState('');
    const { getUsernameById } = useUsers();

    useEffect(() => {
        (async () => {
            try {
                const messageOwner = await getUsernameById(user_id);
                setUsername(messageOwner.username);
                const d = new Date(timestamp);
                const formatedTime = d.getHours() + '.' + d.getMinutes();
                setTime(formatedTime);

            } catch (e) {
                console.log(e.message);
            }
        })();
    }, []);


    return (
        <>
            <ListItem className={classes.root}>
                <Grid container justify="flex-end" style={{ maxWidth: '36ch' }} >
                    <ListItemText
                        primary={
                            <>
                                <Grid container justify="space-between">
                                    <Typography
                                        component="h2"
                                    >
                                        {username}
                                    </Typography>
                                    <Typography
                                        component="span"
                                        variant="subtitle1"
                                        className={classes.timestamp}
                                    >
                                        {time}
                                    </Typography>
                                </Grid>
                            </>
                        }
                        secondary={
                            <>
                                <Typography
                                    component="span"
                                    variant="body2"
                                    className={classes.inline}
                                    color="textPrimary"
                                >
                                    {contents}
                                </Typography>
                            </>
                        }
                    />
                </Grid>

            </ListItem>
        </>
    )
}

export default Message;
