/* eslint-disable react-hooks/exhaustive-deps */
import { Button, Grid, ListItemText, makeStyles, Typography } from "@material-ui/core";
import { useContext, useState } from "react";
import { useEffect } from "react";
import { WebsocketContext } from "../contexts/websocketContext";
import { useChats } from '../hooks/apiHooks';

interface propType {
    id: number,
    setThreadOpen: Function,
    setThreadId: Function,
    threadOpen: Boolean,
}

const useStyles = makeStyles(() => ({
    message: {
        backgroundColor: 'lightGrey',
        marginBottom: '0.5rem',
        borderRadius: '1rem',
        padding: '0.5rem',
    },
    ownMessage: {
        color: 'white',
    },
    inline: {
        display: 'inline',
    },
    inlineOwn: {
        display: 'inline',
        color: 'white',
    },
    timestamp: {
        fontSize: '0.7rem',
        marginLeft: '2rem'
    }
}));

const ThreadButton = ({ id, setThreadOpen, setThreadId, threadOpen }: propType) => {
    const { getThreadName } = useChats();
    const [name, setName] = useState('');
    const { websocket } = useContext(WebsocketContext);
    const classes = useStyles();

    useEffect(() => {
        (async () => {
            try {
                console.log('THREADBUTTON: ', id)
                const threadName = await getThreadName(id)
                console.log('THREADNAME: ', threadName)
                setName(threadName)
            } catch (e) {
                console.log(e.message);
            }
        })();
    }, []);

    const openThread = () => {
        if (!threadOpen) {
            setThreadOpen(true)
            setThreadId(id)
        } else {
            setThreadOpen(false)
            if (websocket !== undefined) {
                websocket.close();
            }
            setThreadId(0)
        }
    }

    return (
        <>
            <Button onClick={openThread} color="primary" variant="contained">
                <Grid container justify="flex-end" >
                    <Grid item className={classes.ownMessage}>
                        <ListItemText
                            primary={
                                <>
                                    <Grid container justify="space-between">
                                        <Typography
                                            component="h2"
                                        >
                                            {name}
                                        </Typography>
                                        <Typography
                                            component="span"
                                            variant="subtitle1"
                                            className={classes.timestamp}
                                        >
                                            hello
                                        </Typography>
                                    </Grid>
                                </>
                            }
                            secondary={
                                <>
                                    <Typography
                                        component="span"
                                        variant="body2"
                                        className={classes.inlineOwn}
                                    >
                                        hello2
                                    </Typography>
                                </>
                            }
                        />
                    </Grid>
                </Grid>
            </Button>
        </>
    )
}

export default ThreadButton
